import React from 'react';
import AwesomeSlider from 'react-awesome-slider';
import Duet from './assets/Duet1'
import 'react-awesome-slider/dist/styles.css';

const MyCarousel = () => {
  return (
    <AwesomeSlider className='carousel'>
      <div>
        <h3 className='projtitle'>Duet</h3>
        <img src="" alt="" />
        <h4 className='projdescription'>Duet is a social app that allows users to connect with others based on their shared interests in music. It features a feed where users can see comments and threads started by others, a connections tab where users can explore profiles they have liked or discover new connections based on their interests, and genre and artist tabs where users can update their interests. The app is built using Sequelize and Express on the backend and React, JavaScript, and CSS on the frontend. Users can get started by creating an account and following prompts to select their favorite music genres and artists, then exploring the feed and connections tab. The app is inspired by sources such as Last.FM, Twitter, and dating sites like Tinder.</h4>
      </div>
      <div>
        <h3 className='projtitle'>Tequila Mockingbird</h3>
        {/* <img src="" alt="" /> */}
        <h4 className='projdescription'>This website is a cocktail search and exploration tool that allows users to browse and search for drinks using an API call with React. The website includes a home page, drink display page, and drink details page, and allows users to search by name or browse by alcohol type, ingredient type, and drink type. It is designed to help people discover new drinks and expand their palate. Technologies used in this project include React, Javascript, and CSS, and the website makes use of an API from (https://www.thecocktaildb.com/api.php). Users can navigate the website using a top navigation bar and can see details and instructions for making specific drinks by clicking on drink or ingredient cards.</h4>
      </div>
      <div>
        <h3 className='projtitle'>Weather or Not</h3>
        {/* <img src="" alt="" /> */}
        <h4 className='projdescription'>Weather or Not is a clothing and accessory database app that helps users determine if they are prepared for the weather by reviewing the forecast for the day and recommending items from their closet. The app is built using Django and Python on the backend and React, JavaScript, CSS, and tailwind on the frontend. The first screen is the Dashboard where users can review the weather forecast to ensure they are prepared for all potential weather conditions.</h4>
      </div>
    </AwesomeSlider>
  );
}

export default MyCarousel;
